<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class First_controller extends CI_Controller {	public function __construct()	{		parent::__construct();	}	public function index()	{		$this->load->view('first_view');	}	public function loadInfo()	{		$this->load->view('info_view');	}	public function loadLogin()	{		$this->load->view('Login_view');	}	public function loadRegisterUser()	{		$this->load->view('Register_view');	}	public function loadDatabase()	{		$this->load->database();		$this->load->dbutil();		if(!$this->dbutil->database_exists("cuccut123database")) {			echo 'none';		} else {			echo 'ket noi thanh cong';		}	}	public function addUsers()	{		$this->load->model('addData_model');				if(!$_POST) {			$this->load->view('Register_view');			return;		} else {			$firstname = $this->input->post('firstname');			$lastname = $this->input->post('lastname');			$name = $lastname . " " . $firstname;			$email = $this->input->post('email');			$password = $this->input->post('password');			$confirmpassword = $this->input->post('comfirmpassword');			$birthdate = $this->input->post('birthdate');			$birthdate = date('Y-m-d',strtotime($birthdate));			$gender = $this->input->post('sex');			$linkfacebook = $this->input->post('linkfacebook');			$imageUrl = $this->addData_model->uploadImage();		}		$patternEmail = '/^(?!(?:(?:\x22?\x5C[\x00-\x7E]\x22?)|(?:\x22?[^\x5C\x22]\x22?)){255,})(?!(?:(?:\x22?\x5C[\x00-\x7E]\x22?)|(?:\x22?[^\x5C\x22]\x22?)){65,}@)(?:(?:[\x21\x23-\x27\x2A\x2B\x2D\x2F-\x39\x3D\x3F\x5E-\x7E]+)|(?:\x22(?:[\x01-\x08\x0B\x0C\x0E-\x1F\x21\x23-\x5B\x5D-\x7F]|(?:\x5C[\x00-\x7F]))*\x22))(?:\.(?:(?:[\x21\x23-\x27\x2A\x2B\x2D\x2F-\x39\x3D\x3F\x5E-\x7E]+)|(?:\x22(?:[\x01-\x08\x0B\x0C\x0E-\x1F\x21\x23-\x5B\x5D-\x7F]|(?:\x5C[\x00-\x7F]))*\x22)))*@(?:(?:(?!.*[^.]{64,})(?:(?:(?:xn--)?[a-z0-9]+(?:-[a-z0-9]+)*\.){1,126}){1,}(?:(?:[a-z][a-z0-9]*)|(?:(?:xn--)[a-z0-9]+))(?:-[a-z0-9]+)*)|(?:\[(?:(?:IPv6:(?:(?:[a-f0-9]{1,4}(?::[a-f0-9]{1,4}){7})|(?:(?!(?:.*[a-f0-9][:\]]){7,})(?:[a-f0-9]{1,4}(?::[a-f0-9]{1,4}){0,5})?::(?:[a-f0-9]{1,4}(?::[a-f0-9]{1,4}){0,5})?)))|(?:(?:IPv6:(?:(?:[a-f0-9]{1,4}(?::[a-f0-9]{1,4}){5}:)|(?:(?!(?:.*[a-f0-9]:){5,})(?:[a-f0-9]{1,4}(?::[a-f0-9]{1,4}){0,3})?::(?:[a-f0-9]{1,4}(?::[a-f0-9]{1,4}){0,3}:)?)))?(?:(?:25[0-5])|(?:2[0-4][0-9])|(?:1[0-9]{2})|(?:[1-9]?[0-9]))(?:\.(?:(?:25[0-5])|(?:2[0-4][0-9])|(?:1[0-9]{2})|(?:[1-9]?[0-9]))){3}))\]))$/iD';		$check = preg_match($patternEmail, $email);		if($check==0) {			$this->load->view('Register_view',array('check'=>$check));			return;		}		$data = array(			'hoten' => $name,			'ngaysinh' => $birthdate,			'linkfacebook' => $linkfacebook,			'gioitinh' => $gender,			'email' => $email,			'password' => $password,			'image' => $imageUrl,		);		// $this->load->view('Register_view', array('data'=>$data));		// $data = $data->result_array();		// var_dump($data);		$this->addData_model->addUserToDB($data, $check);	}	// public function getInfoRegister()	// {	// 	$this->input->post()	// }}/* End of file Firtst_controller.php *//* Location: ./application/controllers/Firtst_controller.php */