<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class First_controller extends CI_Controller {	public function __construct()	{		parent::__construct();	}	public function index()	{		$this->load->helper('url');		$this->load->view('first_view',array("error"=>null,"olddata"=>null));	}	public function displayData()	{		$this->load->helper('url');		$name = $this->input->post('name');		$adress = $this->input->post('adress');		$job = $this->input->post('job');		$note = $this->input->post('note');		$error=[];		$olddata=array("name"=> $name,"adress"=>$adress,"job"=>$job, "note"=>$note);				//$_SESSION["error"] = $error;		if($name=="" || $adress=="" || $job=="" || $note=="") {			$this->load->view('first_view', array("error"=>$error,"olddata"=>$olddata));		} else {			$info['infoUser'] = array($name, $adress, $job, $note);			// echo 'hello';			$this->load->view('Info_view', $info);			}			}}/* End of file Firtst_controller.php *//* Location: ./application/controllers/Firtst_controller.php */