<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class First_controller extends CI_Controller {	public function __construct()	{		parent::__construct();	}	public function index()	{		$this->load->view('first_view');	}	public function loadData()	{				$maSinhVien = $this->input->post('maSinhVien');		$hoTen = $this->input->post('hoTen');		$ngaySinh = $this->input->post('ngaySinh');		$gioiTinh = $this->input->post('gioiTinh');		$noiSinh = $this->input->post('noiSinh');		$lop = $this->input->post('lop');		if($maSinhVien=="" || $hoTen=="" || $ngaySinh=="" || $gioiTinh=="" || $noiSinh=="" || $lop=="") {			$this->load->view('first_view');		} else {			$info['infoUser'] = array($maSinhVien, $hoTen, $ngaySinh, $gioiTinh, $noiSinh, $lop);			// echo 'hello';			$this->load->view('Info_view', $info);			}	}}/* End of file Firtst_controller.php *//* Location: ./application/controllers/Firtst_controller.php */