<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class First_controller extends CI_Controller {	public function __construct()	{		parent::__construct();	}	public function index()	{		$this->load->view('first_view');	}	public function login()	{		$username = $this->input->post('username');		$password = $this->input->post('password');		if($username=='admin' && $password=='123') {			echo 'dang nhap thanh cong';		} else if($username=="" || $password==""){			$this->load->view('first_view');		} else {			echo 'dang nhap that bai';		}	}}/* End of file Firtst_controller.php *//* Location: ./application/controllers/Firtst_controller.php */